import '../ComponentsDoctor/Tumor.css'

import react from 'react';
import SpringApiUrl from '../services/UserServiceApi';
import SpringApiDocsListUrl from '../services/DoctorsList';
import SpringPatientsListApiUrl from '../services/PatientsList';
import NursenavigatorUrl from '../services/NursenavigatorURL';
//Gimport './Tumor.css';
import axios from 'axios';
import DatePicker from 'react-datetime';
import moment from 'moment-timezone';
import 'react-datetime/css/react-datetime.css';
//import ReactMultiSelectCheckboxes from 'react-multiselect-checkboxes';
//import { Multiselect } from 'multiselect-react-dropdown';
//import Select from "react-select";




import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";


class TumorboardDetails extends react.Component {

    constructor(props) {

        var abc = "yes";
        super(props)
        this.state = {
            type: "Diagnosis",
            notes: "",
            nextaction: "",
            date_time: (moment()),
            responsible: "",
            docsList: [],
            nursenav: "",
            priority: "",
            patientIdFk: 0,
            patientName: "",
            feedback: "",
            nurse: [],
            result: {},
            start_time:"",
            name:"",
            tbid:"",
            status:"REQUESTED",
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handledeleteStatus = this.handledeleteStatus.bind(this);
    }



    Changedate = (event) => {
        this.setState({
            date_time: event
        });
    };

    handleInputChange(event) {

        const target = event.target;
        // alert("name:"+target.name) ;
        const value = target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }



    handleChangeAttendingDoc(event) {
        var options = event.target.options;
        var value = [];
        for (var i = 0, l = options.length; i < l; i++) {
            if (options[i].selected) {
                value.push(options[i].value);
            }
        }
        this.setState({ attending_doc: value });
    }



    componentDidMount() {
   
       // SpringApiUrl.getApi().then((response) => {
         //   this.setState({ result: response.data })
        //});
         
        var x =  this.props.location.state.tbid;
        this.setState({tbid:x})
       /*fetch('https://tumorboard-308606.el.r.appspot.com/getTumorboardDetails?id=1')
        .then(response => response.json())
        .then((data) => console.log(data))
        .then((data) => {
            { this.setState({ start_time: data.start_time }); }
            
            // document.getElementById("alertText").style.display = "none";
        })*/

        axios.get('https://tumorboard-308606.el.r.appspot.com/getTumorboardDetails?tbid='+ x)
        .then(res => {
          const pdetails = res.data;
          this.setState({ start_time: pdetails.start_time })
          this.setState({ priority: pdetails.priority })
          this.setState({ patientIdFk: pdetails.patientIdFk })
          this.setState({ patientname: pdetails.patientname })
          this.setState({ name: pdetails.name })
          this.setState({ doctorName: pdetails.doctorName })
          this.setState({ video_link: pdetails.video_link })
          this.setState({ mode: pdetails.mode })
          
          //this.setState({ pthospital: pdetails.hospital })
        })
        axios.get('https://tumorboard-308606.el.r.appspot.com/getDoctorsDetailsByPatientId?patientId='+ x)
        .then(res => {
          const pdetails = res.data;
          this.setState({ attending_doc: pdetails.attending_doc })
        })
    }



        
    handledeleteStatus () {
       // event.preventDefault();
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
             //name: this.state.name,
            // patientIdFk: this.state.patientIdFk,
            //mode: this.state.mode,  
            // purpose: this.state.purpose,
            // start_time: this.state.start_time,
            // requested_time: this.state.start_time,
            // duration: this.state.duration,
            // location: this.state.location,
            // video_link: this.state.video_link,
            // priority: this.state.priority,
            // frequency_in_days: this.state.frequency_in_days,
                status: "COMPLETED"
            // presenting_doc: this.state.presenting_doc,
            // attending_doc: this.state.attending_doc
        }
        //alert(this.state.name)
            
        fetch('https://tumorboard-308606.el.r.appspot.com/update/' + this.state.tbid, {
            method: "PUT",
            body: JSON.stringify(requestOptions),
            headers: { "Content-type": "application/json; charset=UTF-8", "Access-Control-Allow-Origin": "*" }
        })
            .then((response) => {
                if (!response.ok) {
                    alert("There is an error");
                    throw new Error(response.status);
                }
                else {
                    alert("TumorBoard Creation Complete");
                    return;
                }
            })
            .then(json => console.log(json));
    };

    handleSubmit = event => {
       event.preventDefault();
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            name:this.state.tbtype,
            purpose:this.state.purpose,
            start_time: this.state.start_time,
            duration: this.state.duration,
            mode: this.state.mode,
            location: this.state.location,
            video_link: this.state.video_link,
            status: "COMPLETED",
            purpose: this.state.purpose,
            frequency_in_days: this.state.frequency_in_days,
            presenting_doc: this.state.presenting_doc,
            attending_doc: this.state.attending_doc.toString(),
            
        }
            alert(this.state.tbid)
       fetch('https://tumorboard-308606.el.r.appspot.com/update/' + this.state.tbid, {
             method: "PUT",
            body: JSON.stringify(requestOptions),
            headers: { "Content-type": "application/json; charset=UTF-8", "Access-Control-Allow-Origin": "*" }
        })
